using ManagedZLibCompression;
using ManagedCommon.Interfaces;
using System;
using System.CodeDom;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace CompressTests
{
    class Program
    {
		#region
		const string Sound1 = "";
		const string Sound2 = "";
		const string Sound3 = "";
		const string Sound4 = "";
		const string Sound5 = "";

		const string Picture1 = "";
		const string Picture2 = "";
		const string Picture3 = "";
		const string Picture4 = "";
		const string Picture5 = "";

		const string Mouse1 = "dW5kZWZpbmVk";
		#endregion

		static void Main(string[] args)
        {
			string[] soundData = new string[] { Sound1, Sound2, Sound3, Sound4, Sound5 };
			string[] pictureData = new string[] { Picture1, Picture2, Picture3, Picture4, Picture5 };
			ICompression[] compressions = new ICompression[] { new ManagedZLibCompression.ManagedZLibCompression() };

			foreach(ICompression compression in compressions)
			{
				TestCompression(soundData.Select(b64 => Convert.FromBase64String(b64)).ToArray(), compression, "Sound");
				TestCompression(pictureData.Select(b64 => Convert.FromBase64String(b64)).ToArray(), compression, "Picture");
			}

			Console.ReadLine();
		}

		private static void TestCompression(byte[][] datas, ICompression compression, string testName)
		{
			float totalPercents = 0;

			foreach(byte[] data in datas)
			{
			  int totalLength = compression.Compress(data, 3).Length;

			  totalPercents += ((float) totalLength / data.Length) * 100;
			}

			Console.WriteLine($"For test {testName} and compression algorithm {compression.GetType().Name} the average shrink size was {totalPercents / datas.Length}");
		}
    }
}
